'use strict';

class FormErrors{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        if(this.form.vars.errors.length == 0 ){
            return '';
        }

        let str = '<ul>';
        let errors = this.form.vars.errors;
        errors.forEach((error) => {
            str += `<li>${error.message}</li>`;
        });
        str += '</ul>';

        return str;
    }
}

module.exports = FormErrors;