'use strict';

class FormRow{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        let str = '<div>';
        str += this.generator.label(this.form)
            + this.generator.errors(this.form)
            + this.generator.widget(this.form);
        str += '</div>';

        return str;
    }
}

module.exports = FormRow;