'use strict';

class FormRows{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        let str = '';
        let self = this;
        Object.values(this.form).forEach((form) => {
            str += self.generator.row(form);
        });

        return str;
    }
}

module.exports = FormRows;