'use strict';

const WidgetAttributes = require('./widget_attributes');

class FormWidgetSimple{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        if(this.form.type == 'form'){
            return '';
        }

        let type = this.form.type || 'text';
        let str = `<input type="${type}" `;
        str += (new WidgetAttributes(this.form, this.generator)).render();

        if(this.form.vars.value){
            str += ` value="${this.form.vars.value}"`
        }
        str += '/>';

        return str;
    }
}

module.exports = FormWidgetSimple;