'use strict';

const Attributes = require('./attributes');

class WidgetAttributes{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        let str = `id="${this.form.vars.id}" name="${this.form.vars.full_name}"`;
        if(this.form.vars.disabled == true){
            str += ' disabled="disabled"';
        }
        if(this.form.vars.required == true){
            str += ' required="required"';
        }

        str += ' '+ (new Attributes(this.form, this.generator)).render();

        return str;
    }
}

module.exports = WidgetAttributes;