'use strict';

const WidgetChoiceCollapsed = require('./widget_choice_collapsed');
const WidgetChoiceExpanded = require('./widget_choice_expanded');

class WidgetChoice{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        if(this.form.vars.expanded){
            return (new WidgetChoiceExpanded(this.form, this.generator)).render();
        }
        else{
            return (new WidgetChoiceCollapsed(this.form, this.generator)).render();
        }
    }
}

module.exports = WidgetChoice;