'use strict';

const WidgetContainerAttributes = require('./widget_container_attributes');

class WidgetChoiceExpanded{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        let str = '<div ' + (new WidgetContainerAttributes(this.form, this.generator)).render() +'>';
        let self = this;
        Object.values(this.form).forEach((child) => {
            str += self.generator.widget(child);
            str += self.generator.label(child, null, {translation_domain: self.form.vars.choice_translation_domain});
        });
        str += '</div>';
        return str;
    }
}

module.exports = WidgetChoiceExpanded;