'use strict';

const Attributes = require('./attributes');

class WidgetContainerAttributes{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        let str = '';
        if(this.form.vars.id && this.form.vars.id != ''){
            str += ` id="${this.form.vars.id}"`;
        }
        str += (new Attributes(this.form, this.generator)).render();
        return str;
    }
}

module.exports = WidgetContainerAttributes;