'use strict';

const WidgetAttributes = require('./widget_attributes');

class WidgetRadio{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        let value = this.form.vars.value;

        if(this.form.vars.value === true){
            value = 1;
        }
        else if(this.form.vars.value === false || this.form.vars.value === null){
            value = '';
        }

        let str = '<input type="radio" '+ (new WidgetAttributes(this.form, this.generator)).render() + ` value="${value}"`;

        if(this.form.vars.checked){
            str += ' checked="checked"';
        }

        str += '/>';
        return str;
    }
}

module.exports = WidgetRadio;