'use strict';

const WidgetAttributes = require('./widget_attributes');

class WidgetTextarea{
    constructor(form, generator){
        this.form = form;
        this.generator = generator;
    }

    render(){
        let str = '<textarea ' + (new WidgetAttributes(this.form, this.generator)).render()
            + `>${this.form.vars.value}</textarea>`;

        return str;
    }
}

module.exports = WidgetTextarea;